import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const ShoppingCart = () => {
  const { cart, removeFromCart, adjustQuantity, totalPrice, submitCart } = useCart();
  const [isVisible, setIsVisible] = useState(false);

  const toggleCart = () => {
    setIsVisible(!isVisible);
  };

  return (
    <>
      <button className="cart-toggle-btn" onClick={toggleCart}>
        {isVisible ? 'Hide Cart' : 'Show Cart'}
      </button>

      <aside className={`cart-sidebar ${isVisible ? 'visible' : ''}`}>
        <h2>Shopping Cart</h2>
        {cart.map((item) => (
          <div key={item.id} className="cart-item">
            <h3>{item.title}</h3>
            <p>Quantity: {item.quantity}</p>
            <div className="quantity-control">
              <button onClick={() => adjustQuantity(item.id, item.quantity + 1)}>+</button>
              <button onClick={() => adjustQuantity(item.id, item.quantity - 1)}>-</button>
            </div>
            <button className="remove-btn" onClick={() => removeFromCart(item.id)}>
              Remove
            </button>
          </div>
        ))}
        <p>Total: ${totalPrice.toFixed(2)}</p>
        <button className="checkout-btn" onClick={submitCart}>Checkout</button>
      </aside>
    </>
  );
};

export default ShoppingCart;
