import React, { useState } from 'react';

// Avatar component to render initials
const Avatar = ({ firstName, lastName }: { firstName: string; lastName: string }) => {
  // Function to get initials from first name and last name
  const getInitials = (firstName: string, lastName: string) => {
    return `${firstName[0] || ''}${lastName[0] || ''}`.toUpperCase();
  };

  return (
    <div className="avatar">
      <span>{getInitials(firstName, lastName)}</span>
    </div>
  );
};

const UserProfileForm = () => {
  const [form, setForm] = useState({ firstName: '', lastName: '', email: '' });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    alert('Profile updated successfully!');
  };

  return (
    <div className="user-profile">
      <Avatar firstName={form.firstName} lastName={form.lastName} />

      <form onSubmit={handleSubmit}>
        <div>
          <input
            name="firstName"
            value={form.firstName}
            onChange={handleChange}
            placeholder="First Name"
          />
        </div>
        <div>
          <input
            name="lastName"
            value={form.lastName}
            onChange={handleChange}
            placeholder="Last Name"
          />
        </div>
        <div>
          <input
            name="email"
            value={form.email}
            onChange={handleChange}
            placeholder="Email"
          />
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default UserProfileForm;
