import React from 'react';
import { Link } from 'react-router-dom';
import './../App.css';

const Navbar = () => (
  <nav className='navbar'>
    <Link to="/books">Books</Link>
    <Link to="/profile">Profile</Link>
    <button className="cart-toggle-btn" onClick={toggleCartVisibility}>
        {isCartVisible ? 'Hide Cart' : 'Show Cart'}  {/* Toggle button text */}
      </button>

      {/* Conditionally render Shopping Cart */}
      {isCartVisible && <ShoppingCart />}
  </nav>
);

export default Navbar;
