import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const ShoppingCart = () => {
  const [isCartVisible, setIsCartVisible] = useState(false);  // State to control visibility
  const { cart, removeFromCart, adjustQuantity, totalPrice, submitCart } = useCart();

  const toggleCartVisibility = () => {
    setIsCartVisible(!isCartVisible); // Toggle the visibility
  };

  return (
    <div>
      <button className="cart-toggle-btn" onClick={toggleCartVisibility}>
        {isCartVisible ? 'Hide Cart' : 'Show Cart'}  {/* Toggle button text */}
      </button>

      {isCartVisible && (
        <aside>
          <h2>Shopping Cart</h2>
          {cart.map((item) => (
            <div key={item.id} className="cart-item">
              <span>{item.title}</span>
              <span> x {item.quantity}</span>
              <button onClick={() => adjustQuantity(item.id, item.quantity + 1)}>+</button>
              <button onClick={() => adjustQuantity(item.id, item.quantity - 1)}>-</button>
              <button onClick={() => removeFromCart(item.id)}>Remove</button>
            </div>
          ))}
          <p>Total: ${totalPrice.toFixed(2)}</p>
          <button onClick={submitCart}>Checkout</button>
        </aside>
      )}
    </div>
  );
};

export default ShoppingCart;
